import { useMemo } from 'react';
import PropTypes from 'prop-types';
import { MapContainer, 
         TileLayer,
         Marker,
         Tooltip, 
} from 'react-leaflet';
import L from 'leaflet';
import MarkerClusterGroup from 'react-leaflet-cluster'
import Skeleton from '@mui/material/Skeleton';
import { iconTractorIdle, 
         iconTractorOnMission,
         iconTractorUnreachable, 
         iconDroneIdle,
         iconDroneOnMission,
         iconDroneUnreachable ,
} from '../layout/icons/Icon';
import '../styles.css';

const idle_status = 'idle';
const on_mission_status = 'on-mission';
const unreachable_status = 'unreachable';

function getIconByTypeAndStatus(type, status){
    var icon = null;
    switch(status) {
        case idle_status:
           icon = type === 'drone' ? iconDroneIdle : iconTractorIdle;
          break;
        case on_mission_status:
            icon = type === 'drone' ? iconDroneOnMission : iconTractorOnMission;
          break;
        case unreachable_status:
            icon = type === 'drone' ? iconDroneUnreachable : iconTractorUnreachable;
            break;
        default:
      }
    return icon;     
}
function LeafletMap({ data }) {
    const prepareTooltipText = (element) => {
        var tooltipText = <></>
        if(Object.prototype.hasOwnProperty.call(element, '_id') &&
           Object.prototype.hasOwnProperty.call(element, 'type') &&
           Object.prototype.hasOwnProperty.call(element, 'status')) {
            tooltipText = <div className='front-tooltip-class'>
                          <b>Hello {element.type}!</b><br/>
                          Your status is '{element.status}' right now...
                          </div>
        }
        return tooltipText;
    }

    const prepareDisplay = useMemo(
        () => (
            <>
                <MapContainer center={[65.74101998457737, 47.62268066406251]} zoom={14} scrollWheelZoom={true}>
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors' />
                    <MarkerClusterGroup
                        spiderfyDistanceMultiplier={1}
                        showCoverageOnHover={true}
                        maxClusterRadius={150}
                        spiderfyOnMaxZoom={true}
                        polygonOptions={{
                            fillColor: '#ffffff',
                            color: '#f00800',
                            weight: 5,
                            opacity: 1,
                            fillOpacity: 0.8,
                        }}
                    >
                        {data.vehicles.map(element => (
                            <Marker
                                key={element._id}
                                position={[
                                    element.location.latitude,
                                    element.location.longitude
                                ]}
                                draggable={true}
                                icon={getIconByTypeAndStatus(element.type, element.status)}
                            >
                                <Tooltip
                                    opacity={'0.8'}
                                    direction={'top'}
                                    offset={L.point(0, -26)}
                                    closeOnClick={false}
                                    sticky={true}>
                                    {prepareTooltipText(element)}
                                </Tooltip>
                            </Marker>
                        ))}
                    </MarkerClusterGroup>
                </MapContainer>
            </>
        ),[data]);

      return (
        <div>
        { data ? prepareDisplay :  <Skeleton animation="wave" /> }
      </div>
      );
}

LeafletMap.propTypes = {
    data: PropTypes.object,
};

export default LeafletMap;
