import React, { Component, useState, useContext, useEffect } from 'react';
import DataProvider from '../api/DataProvider';
import { MapCalculator } from '../components/MapCalculator';
import LeafletMap from '../layout/LeafletMap';

const configurationData = require("../config.js")

const dataUrl = `${configurationData.web.host}:{configurationData.web.port}`;

function MapContainer() {
    const [dataUrl, setDataUrl] = useState('');

    useEffect(() =>{
        fetch('../config.js').then((response) => response.text())
        .then((data) => {
            const fileLines = data.split('\n')
            setDataUrl(fileLines);
        })
    },[])

    return (
      <>
        <DataProvider calculator={MapCalculator} layout={LeafletMap} url={dataUrl}/>
      </>
    );
}

export default MapContainer;
