const serverportLabelInConf = 'config.web.port';
const servernameLabelInConf = 'config.web.host';

export function getServerAddressFromConf(confData){
    const servername = confData.filter(line => line.includes(servernameLabelInConf))
    .toString().split(' = ')[1].slice(1, -2);

    const serverPort = confData.filter(line => line.includes(serverportLabelInConf))
    .toString().split(' = ')[1].replace(';','');

    return `${servername}:${serverPort}`;
}
