import { useMemo, useState } from 'react';
import PropTypes from 'prop-types';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import { TransitionProps } from '@mui/material/transitions';
import { MapContainer, 
         TileLayer,
         Marker,
         Tooltip, 
} from 'react-leaflet';
import L from 'leaflet';
import MarkerClusterGroup from 'react-leaflet-cluster'
import Skeleton from '@mui/material/Skeleton';
import { iconTractor, iconDrone } from '../layout/icons/Icon';
import '../styles.css';

function LeafletMap({ data }) {
    const [activeVehicle, setActiveVehicle] = useState(null);
    const [open, setOpen] = useState(false);

    const handleClose = () => {
        setOpen(false);
    };

    const prepareTooltipText = (element) => {
        var tooltipText = <></>
        if(Object.prototype.hasOwnProperty.call(element, '_id') &&
           Object.prototype.hasOwnProperty.call(element, 'type') &&
           Object.prototype.hasOwnProperty.call(element, 'status')) {
            tooltipText = <div className='front-tooltip-class'>
                          <b>Hello {element.type}!</b><br/>
                          Your status is '{element.status}' right now...
                          </div>
        }
        return tooltipText;
    }

    const prepareDisplay = useMemo(
        () => (
            <>
                <MapContainer center={[65.74101998457737, 47.62268066406251]} zoom={14} scrollWheelZoom={true}>
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors' />
                    <MarkerClusterGroup
                        spiderfyDistanceMultiplier={1}
                        showCoverageOnHover={true}
                        maxClusterRadius={150}
                        spiderfyOnMaxZoom={true}
                        polygonOptions={{
                            fillColor: '#ffffff',
                            color: '#f00800',
                            weight: 5,
                            opacity: 1,
                            fillOpacity: 0.8,
                        }}
                    >
                        {data.vehicles.map(element => (
                            <Marker
                                key={element._id}
                                position={[
                                    element.location.latitude,
                                    element.location.longitude
                                ]}
                                draggable={true}
                                onClick={() => {
                                    setActiveVehicle(element);
                                    setOpen(true);
                                } }
                                icon={element.type === 'drone' ? iconDrone : iconTractor}
                            >
                                <Tooltip
                                    opacity={'0.8'}
                                    direction={'top'}
                                    offset={L.point(0, -26)}
                                    closeOnClick={false}
                                    sticky={true}>
                                    {prepareTooltipText(element)}
                                </Tooltip>
                            </Marker>
                        ))}
                    </MarkerClusterGroup>
                </MapContainer><Dialog
                    open={open}
                    keepMounted
                    onClose={handleClose}
                    aria-describedby="alert-dialog-slide-description"
                >
                    <DialogTitle>{"Use Google's location service?"}</DialogTitle>
                    <DialogContent>
                        <DialogContentText id="alert-dialog-slide-description">
                            Let Google help apps determine location. This means sending anonymous
                            location data to Google, even when no apps are running.
                        </DialogContentText>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleClose}>Disagree</Button>
                        <Button onClick={handleClose}>Agree</Button>
                    </DialogActions>
                </Dialog>
            </>
        ),[data]);

      return (
        <div>
        { data ? prepareDisplay :  <Skeleton animation="wave" /> }
      </div>
      );
}

LeafletMap.propTypes = {
    data: PropTypes.object,
};

export default LeafletMap;
