import { useState, useEffect } from 'react';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';
import ListSubheader from '@mui/material/ListSubheader';
import IconButton from '@mui/material/IconButton';
import InfoIcon from '@mui/icons-material/Info';
import PropTypes from 'prop-types';
import { useWidth } from '../customHooks/useWidth';
import { ImageContainer } from '../components/ImageContainer';

const avilableSizes = [150, 300, 450, 600];

function TitlebarImageList({ imagesData }) {
  const [selectedImageItem, setSelectedImageItem] = useState(null);
  const [showDot, setShowDot] = useState(false);
  const selectedimagesData = imagesData.slice(0,100);

  const { width: windowWidth } = useWidth();

  const updateImageItem = (item) =>{ 
    console.log(item);
    setSelectedImageItem(item);
  }

  useEffect(() => {
    console.log(selectedImageItem);
    selectedImageItem ? setShowDot(true) : setShowDot(false);
  }, [])

  useEffect(() => {
    console.log(selectedImageItem);
    selectedImageItem ? setShowDot(true) : setShowDot(false);
  }, [selectedImageItem])

  return (
    <>
      { showDot ? <ImageContainer item={selectedImageItem} /> : null }
      <ImageList sx={{ width: windowWidth, height: 1000 }} cols={4} rowHeight={121} variant="quilted">
        <ImageListItem key="Subheader" cols={2}>
          <ListSubheader component="div">Aqua Gallery</ListSubheader>
        </ImageListItem>
        {selectedimagesData.map((item) => (
          <ImageListItem key={item.url} cols={item.cols || 1} rows={item.rows || 1}>
            <img
              src={`${item.url}?w=248&fit=crop&auto=format`}
              srcSet={`${item.url}?w=248&fit=crop&auto=format&dpr=2 2x`}
              alt={item.title}
              loading="lazy"
              onClick={() => updateImageItem(item)}
            />
            <ImageListItemBar
              title={item.title}
              actionIcon={
                <IconButton
                  sx={{ color: 'rgba(255, 255, 255, 0.54)' }}
                  aria-label={`info about ${item.title}`}
                >
                  <InfoIcon />
                </IconButton>
              }
            />
          </ImageListItem>
        ))}
      </ImageList>
    </>
  );
}

TitlebarImageList.propTypes = {
  imagesData: PropTypes.array,
  };

export default TitlebarImageList;

