import L from 'leaflet';

const iconWidth = 40;
const iconHeight = 40;

const myCustomColour = '#00ff00'

const markerHtmlStyles = `
  background-color: ${myCustomColour};
  width: 3rem;
  height: 3rem;
  display: block;
  left: -1.5rem;
  top: -1.5rem;
  position: relative;
  border-radius: 3rem 3rem 0;
  transform: rotate(45deg);
  border: 1px solid #FFFFFF`

const iconTractor = new L.Icon({
    iconUrl: require('../../images/tractor.jpg'),
    iconRetinaUrl: require('../../images/tractor.jpg'),
    iconAnchor: new L.Point(41,41),
    popupAnchor: null,
    shadowUrl: null,
    shadowSize: [41, 41],
    shadowAnchor: null,
    iconSize: new L.Point(iconWidth, iconHeight),
    html: `<span style="${markerHtmlStyles}" />`
});

const iconDrone = new L.Icon({
    iconUrl: require('../../images/drone.png'),
    iconRetinaUrl: require('../../images/drone.png'),
    iconAnchor: null,
    popupAnchor: null,
    shadowUrl: null,
    shadowSize: null,
    shadowAnchor: null,
    iconSize: new L.Point(iconWidth, iconHeight),
    className: 'leaflet-div-icon'
});

export { iconTractor, iconDrone };
