import { useMemo, useState } from 'react';
import PropTypes from 'prop-types';
import { MapContainer, 
         TileLayer,
         Marker,
         Tooltip, 
} from 'react-leaflet';
import L from 'leaflet';
import MarkerClusterGroup from 'react-leaflet-cluster'
import Skeleton from '@mui/material/Skeleton';
import { iconTractor, iconDrone } from '../layout/icons/Icon';
import '../styles.css';

function LeafletMap({ data }) {
    const [activeVehicle, setActiveVehicle] = useState(null);

    const prepareTooltipText = (element) => {
        var tooltipText = <></>
        if(Object.prototype.hasOwnProperty.call(element, '_id') &&
           Object.prototype.hasOwnProperty.call(element, 'type') &&
           Object.prototype.hasOwnProperty.call(element, 'status')) {
            tooltipText = <div className='front-tooltip-class'>
                <b>Hello {element.type}!</b><br/>
                Your status is '{element.status}' right now...
            </div>
        }
        return tooltipText;
    }

    const prepareDisplay = useMemo(
        () => (
            <MapContainer center={[55.74101998457737, 37.62268066406251]} zoom={14} scrollWheelZoom={false}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                />
                 <MarkerClusterGroup
                    spiderfyDistanceMultiplier={1}
                    showCoverageOnHover={false}
                    onClick={(e) => console.log('onClick', e)}
                    maxClusterRadius={150}
                    spiderfyOnMaxZoom={true}
                    polygonOptions={{
                        fillColor: '#ffffff',
                        color: '#f00800',
                        weight: 5,
                        opacity: 1,
                        fillOpacity: 0.8,
                    }}
                    showCoverageOnHover={true}
                >
                {data.vehicles.map(element => (
                    <Marker
                        key={element._id}
                        position={[
                            element.location.latitude,
                            element.location.longitude
                        ]}
                        draggable={true}
                        onClick={() => {
                            setActiveVehicle(element);
                        }}
                        icon={element.type === 'drone' ? iconDrone : iconTractor}
                    >
                        <Tooltip 
                            opacity={'0.8'}
                            direction={'top'}
                            offset={L.point(0, -26)}
                            closeOnClick={false}
                            sticky={true}>
                                {prepareTooltipText(element)}
                        </Tooltip>
                    </Marker>
                ))}
            </MarkerClusterGroup>
          </MapContainer>
        ),[]);

      return (
        <div>
        { data ? prepareDisplay :  <Skeleton animation="wave" /> }
      </div>
      );
}

LeafletMap.propTypes = {
    data: PropTypes.object.isRequired,
};

export default LeafletMap;
